------------------------------------------------------------------------------
--	 Add trigger on ReviseResourceKey table
------------------------------------------------------------------------------
IF EXISTS (SELECT name FROM sysobjects
      WHERE name = 'tr_ReviseResourceKey' AND type = 'TR')
   DROP TRIGGER tr_ReviseResourceKey
GO


CREATE TRIGGER [dbo].[tr_ReviseResourceKey]
on [dbo].[ReviseResourceKey]
	FOR INSERT
	AS 
	update dbo.ReviseResourceKey set createddate = GETDATE()
	where resourcekeyid in (select resourcekeyid from inserted)
GO
------------------------------------------------------------------------------
--	 procedure to insert Diffuse text in CMS database
------------------------------------------------------------------------------

IF  EXISTS (SELECT * FROM sys.objects WHERE object_id = OBJECT_ID(N'[pAT_insertDiffuseText]') AND type in (N'P', N'PC'))
DROP PROCEDURE [pAT_insertDiffuseText]
GO

SET ANSI_NULLS ON
GO

SET QUOTED_IDENTIFIER ON
GO

CREATE procedure [pAT_insertDiffuseText]
	@pGroup nvarchar(255),
	@pResourceKey nvarchar(255),
	@pAllowHTML bit,
	@pKeyTitle nvarchar(255),
	@pResourceValue nvarchar(max)
as
begin
	IF NOT EXISTS (SELECT * FROM ReviseResourceKey WHERE ResourceKey = @pResourceKey AND ResourceType='DiffuseSources')
		BEGIN
			INSERT INTO ReviseResourceKey([ResourceKey],[ResourceType],[AllowHTML],[KeyDescription],[KeyTitle],[ContentsGroupID])
				VALUES(
				@pResourceKey, 
				'DiffuseSources',
				@pAllowHTML,
				'',
				@pKeyTitle,
				(select LOV_ContentsGroupID from LOV_ContentsGroup where Code = @pGroup)
				)
				
			INSERT INTO [dbo].[ReviseResourceValue]([ResourceKeyID],[CultureCode],[ResourceValue])
				VALUES(
				(select ResourceKeyID from [dbo].[ReviseResourceKey] where [ResourceKey]=@pResourceKey AND [ResourceType]='DiffuseSources'),
				'en-GB',
				@pResourceValue
				)

		END
end
GO


